package Org.totalBeginner.tutorial;

public class Addition {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		int b=20;
		int res=a+b;
		System.out.println("result is : "+ res);
		
		System.out.println(++a);
		System.out.println(a++);
		System.out.println(a--);

	}

}
